beforeSaveCustomScript:
$callStatus = list("Call Back","Did not pick up");

if (entity\isAttributeChanged('cCallStatus')) {



    if (array\includes($callStatus, cCallStatus)) {

            $name = string\concatenate('Call -', firstName);
            $name = string\concatenate($name, ' ');
            $name = string\concatenate($name, lastName);

            $callExists = record\exists('Call', 'name=', $name);


            $dateTime = datetime\today();
            $dateTime = datetime\addDays($dateTime, 3);
            $dateTime = datetime\addHours($dateTime, 7);

            $callObject = object\create();

            if ($callExists) {
                $callObject = record\fetch('Call', record\findOne("Call", "createdAt", "desc", "name=", $name));
                object\set($callObject, 'dateStart', $dateTime);

                record\update("Call", $existingCallId, $callObject);

            } else {

                object\set($callObject, 'dateStart', $dateTime);
                object\set($callObject, 'name', $name);

                $reminderObject = object\create();
                object\set($reminderObject, 'type', 'Popup');
                object\set($reminderObject, 'seconds', 0);

                object\set($callObject, 'reminders', list($reminderObject));


                $callId = record\create("Call", $callObject);
                record\relate('Call', $callId , 'assignedUser', assignedUserId);
                record\relate('Call', $callId , 'teams', teamsIds);
                record\relate('Lead', id, 'calls', $callId);


            }    

    } else if (cCallStatus == "Wrong Info" || cCallStatus == "Not Interested") {
        status = "Lost";
    } else if (cCallStatus == "Interested") {
        status = "Qualified";

    }     


}
